import React, { useState } from 'react';
import SearchList from './SearchList';
import axios from 'axios';

const SearchForm = props => {
  const [searchQuery,setSearchQuery] = useState('');
  const [quotes, setQuotes] = useState('');
  const [quote, setQuote] = useState('');

  const fetchQuotes = e => {
    e.preventDefault();
    axios.get('/quotes').then(response => {
      // setQuotes(response.data);
      let arr = Array.from(response.data);
      arr.map(quote => ({...quote, id: Math.floor(Math.random() * 1000)}))
      setQuotes(arr);
      console.log(arr);
      // let quotes = arr.forEach(quote => {
      //   setQuote({
      //   anime: quote.anime,
      //   // character: quote.character,
      //   quote: quote.quote,
      //   id: Math.floor(Math.random() * 1000)
      //   })
      // });
      // obj.key3 = "value3";
    }).catch(err => console.log(err));
  };

  // const newArr = quotes.forEach(quote => ({...quote, id: Math.floor(Math.random() * 1000)}))
  // console.log(newArr);

  const inputHandler = e => {
    setSearchQuery(e.target.value);
  };

  return (
    <div>
      <form>
        <input onChange={inputHandler} type="text" name="title" placeholder="Search by name" value={searchQuery}/>
        <button onClick={fetchQuotes}>search</button>
      </form>
      <ul>
        {quotes ? quotes.map((listItem, index) =>
          <SearchList quote={listItem} key={index} listId={Math.floor(Math.random() * 1000)} listAnime={listItem.anime} listCharacter={listItem.character} listQuote={listItem.quote} favouriteList={props.favouriteList} setFavouriteList={props.setFavouriteList}/>
        ) : ''}
      </ul>
    </div>
  )
}

export default SearchForm;
