import React, { useState } from 'react';
import axios from 'axios';
import logo from '../logo.svg';
import QuoteCard from './QuoteCard';

const Quote = props => {
  const [quote, setQuote] = useState('');

  const fetchQuote = () => {
    axios.get('/quote').then(response => {
      setQuote({
        anime: response.data.anime,
        character: response.data.character,
        quote: response.data.quote,
        id: Math.floor(Math.random() * 1000)
      });
    }).catch(err => {
      console.log(err);
    });
  };

  console.log(quote);

  return (
    <div>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={fetchQuote}>get quotes</button>
      </header>
      <ul>
        {quote ? quote.map((listItem, index) =>
          <QuoteCard  key={index} listId={listItem.id} listAnime={listItem.anime} listCharacter={listItem.character} listQuote={listItem.quote} favouriteList={props.favouriteList} setFavouriteList={props.setFavouriteList}/>
        ) : ''}
      </ul>
    </div>
  )
}

export default Quote;
