import React, { useState } from 'react';
import SearchList from './SearchList';
import axios from 'axios';

const SearchForm = props => {
  const [searchQuery,setSearchQuery] = useState('');
  const [quotes, setQuotes] = useState('');
  const [quote, setQuote] = useState('');

  const fetchQuotes = e => {
    e.preventDefault();
    axios.get('/quotes').then(response => {
      setQuotes(response.data);
      const arr = [response.data];
      arr.map(quote => setQuote({
        anime: quote.anime,
        character: quote.character,
        quote: quote.quote,
        id: Math.floor(Math.random() * 1000)
      }));
    }).catch(err => console.log(err));
  };

  console.log(quote);

  const inputHandler = e => {
    setSearchQuery(e.target.value);
  };

  return (
    <div>
      <form>
        <input onChange={inputHandler} type="text" name="title" placeholder="Search by name" value={searchQuery}/>
        <button onClick={fetchQuotes}>search</button>
      </form>
      <ul>
        {quotes ? quotes.map((listItem, index) =>
          <SearchList  quote={listItem} quotes={quotes} key={index} listId={listItem.id} listAnime={listItem.anime} listCharacter={listItem.character} listQuote={listItem.quote} favouriteList={props.favouriteList} setFavouriteList={props.setFavouriteList}/>
        ) : ''}
      </ul>
    </div>
  )
}

export default SearchForm;
