import React from 'react';

const QuoteCard = props => {

  const handleAddFavourite = props => {
    props.setFavouriteList(favouriteList => [...favouriteList, props.quotes]);
  }

  return (
    <div>
      <h1>{props.anime}</h1>
      <h1>{props.character}</h1>
      <h1>{props.quote}</h1>
      <button onClick={handleAddFavourite}>add to favourite</button>
    </div>
  )
}

export default QuoteCard;
