import React, { useState } from 'react';
import axios from 'axios';
import logo from '../logo.svg';

const Quote = () => {
  const [quote, setQuote] = useState('');
  const [favouriteList,setFavouriteList] = useState('');

  const handleBtnClick = () => {
    axios.get('/quote').then(response => {
      setQuote({
        anime: response.data.anime,
        character: response.data.character,
        quote: response.data.quote
      });
    }).catch(err => {
      console.log(err);
    });
  };

  const getFromLocalStorage = () => {
    let data = window.localStorage.getItem('favouriteList');
    return data ? JSON.parse(data) : { favouriteList: [] };
  }

  const setLocalStorage = favouriteItem => {
    const data = getFromLocalStorage();
    console.log(data);
    data.search.push(favouriteItem);
    window.localStorage.setItem('favouriteList', JSON.stringify(data));
  }

  const handleAddFavourite = (event) => {
    event.preventDefault();
    setLocalStorage(quote);
    console.log(window.localStorage);
  }

  return (
    <div>
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <button onClick={handleBtnClick}>get quotes</button>
        <h1>{quote.anime}</h1>
        <h1>{quote.character}</h1>
        <h1>{quote.quote}</h1>
        <button onClick={handleAddFavourite}>add to favourite</button>
      </header>
    </div>
  )
}

export default Quote;
